fn check_has_digit(password: & String) -> bool {
    for ch in password.chars(){
        if ch as u8 >= 48 && ch as u8 <= 57 {
            return true
        }
    }
    return false
}

fn check_has_lowercase(password: & String) -> bool {
    for ch in password.chars(){
        if ch as u8 >= 97 && ch as u8 <= 122 {
            return true
        }
    }
    return false
}

fn check_has_uppercase(password: & String) -> bool {
    for ch in password.chars(){
        if ch as u8 >= 65 && ch as u8 <= 90 {
            return true
        }
    }
    return false
}

fn find_sequences(password: & String) -> Vec<i32> {
    /*
        Finds all unsafe sequences and returns vector of their lengths
    */
    let mut seq: i32 = 1;
    let mut sequences: Vec<i32> = Vec::new();
    if password.len() < 3 {
        return sequences
    }
    let mut prev: char = password.chars().next().unwrap();
    for ch in password.chars().skip(1) {
        if ch == prev {
            seq += 1
        }
        else {
            if seq >= 3 {
                sequences.push(seq);
            }
            seq = 1
        }
        prev = ch;
    }
    if seq >= 3 {
        sequences.push(seq);
    }
    return sequences
}


impl Solution {
    pub fn strong_password_checker(password: String) -> i32 {
        /*
            Core ideas:
            1. We can utilize same action for solving multiple problems
            2. Each insert can reduce sequence size by 2
            3. For each sequence we need length // 3 replacements
            4. We can solve unsafe sequences with deletions but we need to delete chars from the sequenses with least (length % 3) value.

            FLow:
            1. We fix password length, so we know how many insertions/deletions we have.
            2. We utilize deletions deleting per 1 char from sequences with least (length % 3).
            3. We utilize insertions deleting per 2 characters from sequence.
            4. All unsolved sequences we solve with replacements
            5. We fix problem with missing characters with additional replacements (if needed).
        */
        let length = password.len() as i32;
        let mut char_actions: i32 = 0;
        if !check_has_digit(&password) {char_actions += 1};
        if !check_has_lowercase(&password) {char_actions += 1};
        if !check_has_uppercase(&password) {char_actions += 1};
        let mut insertions: i32 = 0;
        let mut deletions: i32 = 0;
        let mut replations: i32 = 0;

        // fix length
        if length > 20 {
            deletions += length - 20;
        }
        if length < 6 {
            insertions += 6 - length;
        }

        // find unsafe sequences
        let mut sequences = find_sequences(&password);

        // utilize deletions
        if deletions > 0 {
            let mut i = deletions;
            loop {
                sequences.sort_by_key(|x| x % 3);
                let i0 = i;
                for s in 0..sequences.len(){
                    if sequences[s] < 3 {continue}
                    sequences[s] -= 1;
                    i -= 1;
                    break
                }
                if i == 0 || i == i0 { break }
            }
        }

        // utilize insertions
        if insertions > 0 {
            let mut i = insertions;
            loop {
                let i0 = i;
                for s in 0..sequences.len(){
                    if sequences[s] < 3 {continue}
                    sequences[s] -= 2;
                    i -= 1;
                    if i == 0 { break }
                }
                if i == 0 || i == i0 { break }
            }
        }

        // fix all other sequences with replacements
        let mut seq_operations = 0;
        for seq in sequences {
            if seq >= 3 {
                seq_operations += seq / 3;
            }
        }
        replations += seq_operations;

        // fix missing characters
        char_actions = char_actions - replations - insertions;
        if char_actions < 0 {char_actions = 0};
        replations += char_actions;

        return deletions + replations + insertions
    }
}
