        import re
        groups = [match.group(0) for match in re.finditer(r'(.)\1*', password) if len(match.group(0)) > 2]

        if len(password) <= 20:
            for group in groups:
                steps += len(group) // 3
                must_add -= len(group) // 3

        if len(password) <= 20:
            must_add = max(must_add, 0)
            if len(password) + steps >= 6:
                steps += must_add
            else:
                steps += max(must_add, 6 - (len(password) + steps))

        if len(password) > 20:
            must_remove = len(password) - 20
            lengths = [None] * len(groups)
            plus = [None] * len(groups)
            for i in range(1, 4):
