                    if plus[k] is None:
                        plus[k] = 0
                    chL = len(groups[k]) - plus[k]
                    if lengths[k] is None:
                        lengths[k] = chL

                    def rec():
                        nonlocal steps, must_remove
                        nonlocal chL, lengths
                        if (chL - i) // 3 < chL // 3 and len(password) - steps - i >= 6 and must_remove >= i and chL > 2 and lengths[k] - i > 0:
                            steps += i
                            plus[k] += i
