class Solution:
    def findMaximumXOR(self, nums):
        ans, mask = 0, 0
        for i in range(31, -1, -1):
            mask |= 1<<i
            found = set([num & mask for num in nums])

            start = ans | 1<<i
            for pref in found:
                if start^pref in found:
                    ans = start
                    break

        return ans
