    {
        TrieNode* curr = this;
        for(int i=30;i>=0;i--)
        {
            bool bit = n & (1<<i);
            if(curr->binary[!bit])
                curr = curr->binary[!bit];
            else 
                curr = curr->binary[bit];
        }
        return curr->val;
    }
};


