class Solution {
public:
    using VI = vector<int>;
    int findMaximumXOR(VI& A, NodePtr root = make_shared<Node>(), int best = 0) {
        for (auto x: A) {
            auto cand = 0;
            auto cur = root,
                 alt = root;
            for (auto i{ 31 }; 0 <= i; --i) {
                auto p = x & (1 << i) ? 1 : 0,
                     q = p ^ 1;
                if (cur->m.find(p) == cur->m.end())
                    cur->m[p] = make_shared<Node>();
                cur = cur->m[p];
                if (alt->m.find(q) != alt->m.end())
                    alt = alt->m[q], cand ^= 1 << i;
                else
                    alt = alt->m[p];
            }
            best = max(best, cand);
        }
        return best;
    }
};
