class TrieNode:
    def __init__(self):
        self.children = defaultdict(TrieNode)
        self.val = 0

class Trie:
    def __init__(self):
        self.root = TrieNode()

    def insert(self, binary_str, num):
        curr = self.root

        for bit in binary_str:
            curr = curr.children[int(bit)]

        curr.val = num

    def seacrh(self, binary, num):
        curr = self.root
        for bit in binary:
            d = int(bit)
            want = d ^ 1
            if want in curr.children:
                curr = curr.children[want]
            else:
                curr = curr.children[want ^ 1]

        return curr.val ^ num


class Solution(object):
    def findMaximumXOR(self, nums):
        """
        :type nums: List[int]
        :rtype: int
        """
        trie = Trie()
        max_xor = float("-inf")

        for num in nums:
            binary_str = bin(num)[2:].zfill(32)
            trie.insert(binary_str, num)

            max_xor = max(max_xor, trie.seacrh(binary_str, num))

        return max_xor

