class Solution {
public:
    int findMaximumXOR(vector<int>& nums) {
        int n = nums.size();
        int max1 = 0;
        int mask = 0;
        unordered_set<int> s;

        for(int i = 31; i>=0; i--){
            mask |= (1<<i);

            for(int j = 0; j<n; j++){
                s.insert(mask & nums[j]);
            }

            int possible = max1 | (1<<i);

            for(auto j : s){
                if(s.find(j ^ possible) != s.end()){
                    max1 = possible;
                    break;
                }
            }

            s.clear();
        }
        return max1;
    }
};
```

