    void insert(int a,Node*root){

         Node*curr=root;

         for(int i=31;i>=0;i--){
              int last_bit=(a>>i) &1;
               if(last_bit==0){

                 if(curr->left==NULL){

                     curr->left=new Node();

                 }
                   curr=curr->left;


               }else{
                   if(curr->right==NULL){
                       curr->right=new Node();
                   }
                   curr=curr->right;
               }
         }
        return;

