        int maxXOR = 0;
        for (int i = 31; i >= 0; i--) {
            int val = (num >> i) & 1;
            if (val == 1) {
                if (ptr.zero != null) {
                    ptr = ptr.zero;
                    maxXOR += (1 << i);
                } else {
                    ptr = ptr.one;
                }
            } else {
                if (ptr.one != null) {
                    ptr = ptr.one;
                    maxXOR += (1 << i);
                } else {
                    ptr = ptr.zero;
                }
            }
        }
        return maxXOR;
    }

    class Node {
        Node zero;
        Node one;
    }
}
