class Trie {
public:
    Trie() 
    {
        next[0] = next[1] = nullptr;
    }

	// destructor has a huge impact to run time.
    ~Trie()
    {
        if (next[0]) {
            delete next[0];
        }

        if (next[1]) {
            delete next[1];
        }
    }

    void insert(int n) {
        Trie* cur = this;

        for (int i = 31; i >= 0; --i) {
            if (n & (1 << i)) {
                if (!cur->next[1]) {
                    cur->next[1] = new Trie();
                }
                cur = cur->next[1];
            } else {
                if (!cur->next[0]) {
                    cur->next[0] = new Trie();
                }
                cur = cur->next[0];
            }
        }

        cur->val = n;
    }

    int findOpposite(int n) {
        Trie* cur = this;

        for (int i = 31; i >= 0; --i) {
            if (n & (1 << i)) {
                if (cur->next[0]) {
                    cur = cur->next[0];
                } else {
                    cur = cur->next[1];
                }
            } else {
                if (cur->next[1]) {
                    cur = cur->next[1];
                } else {
                    cur = cur->next[0];
                }
            }
        }

        return cur->val;
    }
private:
    int val;
    Trie* next[2];
};

class Solution {
public:
    int findMaximumXOR(vector<int>& nums) {
        int ans = 0;

        Trie root;

        for (auto n : nums) {
            root.insert(n);
        }

        for (auto n : nums) {
            ans = max(ans, n ^ root.findOpposite(n));
        }

        return ans;
    }
};
