class TrieNode:
    def __init__(self):
        self.children = dict()
        self.is_word = False

class MagicDictionary:

    def __init__(self):
        """
        Initialize your data structure here.
        """
        self.root = TrieNode()

    def insert_word(self, word):
        node = self.root
        for ch in word:
            if ch not in node.children:
                node.children[ch] = TrieNode()
            node = node.children[ch]

        node.is_word = True

    def buildDict(self, dict: List[str]) -> None:
        """
        Build a dictionary through a list of words
        """
        for word in dict:
            self.insert_word(word)

    def search(self, word: str) -> bool:
        """
        Returns if there is any word in the trie that equals to the given word after modifying exactly one character
        """
        return self.dfs(self.root, 0, word, 0)

    def dfs(self, node, index, word, count): # count of mismatch
        if count > 1:
            return False

        if len(word) == index:
            return node.is_word and count == 1

        for ch in node.children:
            if self.dfs(node.children[ch], index + 1, word, count + (word[index] != ch)):
                return True

        return False
```

**1268. Search Suggestions System**

```
class TrieNode:
    def __init__(self):
        self.children = {}
        self.is_word = False

class Trie:
    def __init__(self):
        self.root = TrieNode()

    def insert(self, word):
        node = self.root
        for ch in word:
            if ch not in node.children:
                node.children[ch] = TrieNode()
            node = node.children[ch]

        node.is_word = True

    def startsWith(self, prefix): # return top three matches if exist
        node = self.root
        top_three = []
        for ch in prefix:
            if ch not in node.children:
                return []
            node = node.children[ch]

        return self.dfs(node, prefix, [])


    def dfs(self, node, prefix, words):
        if node.is_word:
            words.append(prefix)

        for ch in node.children:
            self.dfs(node.children[ch], prefix + ch, words)
        return words


class Solution:
    def suggestedProducts(self, products: List[str], searchWord: str) -> List[List[str]]:

        products.sort()
        trie = Trie()
        for product in products:
            trie.insert(product)

        res = []
        for i in range(len(searchWord)):
            res.append(trie.startsWith(searchWord[:i+1])[:3])

        return res
```

