public class Solution 
{
    public int FindMaximumXOR(int[] nums) 
    {
        // All positive so we can start with 30
        // no more than 31 so we can use an integer for the mask
        // if a^b=c then a=b^c

        var mask = 0;

        for (int i = 30; i >= 0; i--)
        {
            var contain1Set = new HashSet<int>();
            var contain0Set = new HashSet<int>();
            foreach (var number in nums)
            {
                if ((number >> i & 1) == 1)
                {
                    // i + 1 means the last one.
                    contain1Set.Add(number >> (i + 1));
                }
                else
                {
                    contain0Set.Add(number >> (i + 1));
                }
            }

            if (contain1Set.Count == 0 || contain0Set.Count == 0) continue;

            // get zero one
            foreach (var numberWithMask in contain0Set)
            {
                var belongTo1Set = ((mask >> (i + 1)) ^ numberWithMask);

                if (contain1Set.Contains(belongTo1Set))
                {
                    mask |= (1 << i);
                    break;
                }
            }
        }

        return mask;
    }
}
```

