class Solution {
    TreeNode root;
    class TreeNode {
        int val;
        public TreeNode[] children;
        public TreeNode(){
            children = new TreeNode[2];
        }
    }
    public void insert(int word) {
        TreeNode cur = root;
        for(int i=31;i>=0;i--) {
            int j = ((word >>>i)&1) == 0?0:1;
            TreeNode child = cur.children[j];
            child = child == null?new TreeNode():child;
            cur.children[j] = child;
            cur = child;
        }
        cur.val = word;
    }
    public int getLarget(int prefix) {
        TreeNode cur = root;
        for(int i=31;i>=0;i--) {
            int j = ((prefix >>>i)&1 ) == 0?1:0;
            if(cur.children[j] == null) j = 1-j;
            TreeNode child = cur.children[j];
            cur = child;
        }
        return cur.val;
    }

    public int findMaximumXOR(int[] nums) {
        root = new TreeNode();
        for(int x:nums) insert(x);
        int max = 0;
        for(int x:nums) {
            max = Math.max(max,x^getLarget(x));
        }
        return max;
    }
}
