    {
        children[0] = NULL;
        children[1] = NULL;
    }
};

class Trie
{
public:
    TrieNode *root;
    Trie()
    {
        root = new TrieNode();
    }
    void insert(int n)
    {
        TrieNode *node=root;
        for (int i = 31; i >= 0; i--)
        {
            int index = ((n >> i) & (1));

            if (node->children[index] != NULL)
            {
                node = node->children[index];
            }
            else
            {
                TrieNode *child = new TrieNode();
                node->children[index] = child;
                 node = node->children[index];
            }
        }
    }

    int maxXor(int n)
    {
        TrieNode *node=root;
        int ans = 0;
        for (int i = 31; i >= 0; i--)
        {
            int index = ((n >> i) & (1));

            if (node->children[1 - index] != NULL)
            {
                node = node->children[1 - index];
                ans += (1 << i);
            }
            else
            {
                node = node->children[index];
            }
        }
        return ans;
    }
};
class Solution
{
public:
    int findMaximumXOR(vector<int> &arr)
    {

        int N = arr.size();
        if (N == 1)
            return 0;
        Trie *Node = new Trie();
        int ans = 0;
        for (int i = 0; i < N; i++)
        {
            Node->insert(arr[i]);
        }

        for (int i = 0; i < N; i++)
        {
            ans = max(ans, Node->maxXor(arr[i]));
        }
        return ans;
    }
};
