		}

		void Insert(int num){
			TrieNode* curr = root;
			for(int i = 31 ; i >= 0 ; i--){
			   int bit = (1 << i)&num;
			   if(bit == 0){
				  if(curr -> left == nullptr){
					 curr -> left = new TrieNode();
				  }
				  curr = curr -> left;
			   }else{
				  if(curr -> right == nullptr){
					 curr -> right = new TrieNode();
				  }
				  curr = curr -> right;
			   }
			}
		}

		int MaxXorPair(int n , vector<int> v){

			for(int i = 0 ; i < n ; i++){
				Insert(v[i]);
			}

			int Xor = INT_MIN;
			for(int i = 0 ;i < n ; i++){
				int curr_xor = 0;
				TrieNode* curr = root;
				for(int j = 31 ;j >= 0 ; j--){
					int bit = (1 << j) & v[i];
					if(bit == 0){
						if(curr -> right != nullptr){
							curr_xor += (1 << j);
							curr = curr -> right;
						}else{
							curr = curr -> left;
						}
					}else{
						if(curr -> left != nullptr){
							curr_xor += (1 << j);
							curr = curr -> left;
						}else{
							curr = curr -> right;
						}
					}
				}
				Xor = max(Xor , curr_xor);
			}
			return Xor;
		}
};


