struct Node {
    Node * links[2] ;
    bool flag = false; 

    bool containsKey(int bit){
        return links[bit] != NULL ;
    }
    void putKey(int bit , Node * node){
        links[bit] = node ;
    }
    Node * nextNode(int bit){
        return links[bit] ;
