class Trie {
    private: Node* root ;
    public:  
    Trie(){
        root = new Node() ;
    }
    void insert(int n){
        Node * node = root ;
        for(int i = 31 ;i>=0 ; i--){
            int bit = (n>>i) & 1 ;
            if(!node->containsKey(bit)){
                node->putKey(bit, new Node()) ;
            }
            node = node->nextNode(bit) ;
        }
        node->setEnd();
    }
    int search(int n){
        Node * node = root ;
        int ans = 0 ;
        for(int i = 31 ;i>=0; i--){
            int bit = (n>>i) & 1 ;
            if(node->containsKey(1-bit)){
                ans = ans ^ (1<<i) ;
                node = node->nextNode(1-bit);
            }
            else {
                node = node->nextNode(bit);
            }
        }
        return ans ;
    }  
};


