approach - for current array number , we'll try to find it's max xor pair number by maximizing 1 in msb of xor pair result . 
first we insert number in the trie in its binary form , then we find max xor for current number, then compare curr ans with max obtained till now and then update accordingly.
while searching for current max xor ,  we see the current msb and try to find complementary bit , if we find complementary bit then we add pow(2,position of current bit) otherwise we dont.
```
class Node{
 public:
    Node* left; //for 0
    Node* right; //for 1
};

class Trie{
public:

    Node* root ;

    Trie(){
        root = new Node() ;
    }

    void insert(int num){
        Node* temp = root ;

        //32 bit number
        for(int i=31 ; i>=0 ; i--){
            int bit = (num>>i)&1 ;

            if(bit==0){
                if(!temp->left){
                    temp->left = new Node() ;
                }

                temp=temp->left ;
            }else{
                //bit is 1
                if(!temp->right){
                    temp->right = new Node() ;
                }
                temp=temp->right ;
            }
        }

    }

    int max_xor_helper(int num){
        Node* temp = root ;

        int curr_ans = 0 ;

        for(int i=31 ; i>=0 ; i--){
            int bit = (num>>i)&1 ;

            if(bit==0){
                //find complementary bit 1 ;
                if(temp->right){
                    curr_ans += (1<<i) ;
                    temp=temp->right ;
                }else{
                    temp=temp->left ;
                }
            }else{
                //bit is 1 , find 0 
                if(temp->left){
                    curr_ans += (1<<i) ;
                    temp=temp->left ;
                }else{
                    temp=temp->right ;
                }
            }
        }

        return curr_ans ;
    }

};


class Solution {
public:
    int findMaximumXOR(vector<int>& nums) {
        Trie* t = new Trie() ;

        int ans = 0 ;

        for(int i=0 ; i<nums.size() ;i++){
            t->insert(nums[i]) ;
            int curr_xor = t->max_xor_helper(nums[i]) ;
            ans = max(curr_xor , ans) ;
        }

        return ans ;
    }
};
