class Node{
    Node left;
    Node right;
    int val;
    Node(){
        this.left = null;
        this.right = null;
        // this.val = 0;
    }

}

class Solution {

    // add a number to the Node
    void insert(int n, Node head)
    {
        Node current = head;
        for(int i = 31; i >= 0; i--)
        {
            // get the LSB
            int bit = (n >> i) & 1;
            if ( bit == 0 )
            {
                if ( current.left == null )
                    current.left = new Node();

                current = current.left;
            }else{
                if ( current.right == null )
                    current.right = new Node();

                current = current.right;
            }
        }
    }


    int findPair(int n, Node head)
    {
        Node current = head;
        int xor = 0;

        for(int i = 31; i >= 0; i--)
        {
            // get the LSB
            int bit = (n >> i) & 1;
            if ( bit == 1 )
            {
                if ( current.left != null ){
                    current = current.left;
                    xor += Math.pow(2, i);
                }
                else
                    current = current.right;
            }else{

                if ( current.right != null ){
                    xor += Math.pow(2, i);
                    current = current.right;
                }
                else
                    current = current.left;
            }
        }


        return xor;
    }


    public int findMaximumXOR(int[] nums) {

        // a number that is complement to that number is the 
        // maximum xor for that number

        Node head = new Node();

        for(int n : nums)
            insert(n, head);

        // find the maximum xor pair

        int max = 0;

        for(int i =0; i< nums.length; i++)
        {
            int temp = findPair(nums[i], head);
            max = Math.max(max, temp);
        }

        return max;
    }
}
