                    xor += Math.pow(2, i);
                }
                else
                    current = current.right;
            }else{

                if ( current.right != null ){
                    xor += Math.pow(2, i);
                    current = current.right;
                }
                else
                    current = current.left;
            }
        }


        return xor;
    }


    public int findMaximumXOR(int[] nums) {

        // a number that is complement to that number is the 
        // maximum xor for that number

        Node head = new Node();

        for(int n : nums)
            insert(n, head);

        // find the maximum xor pair

        int max = 0;

        for(int i =0; i< nums.length; i++)
        {
            int temp = findPair(nums[i], head);
            max = Math.max(max, temp);
        }

        return max;
    }
}
