                int greedyGuess = max | (1 << i);
                // try to find a pair that is able to  become the greedyguess after xor 
                for (int num: set ) {
                    int otherNumRequired =  num ^ greedyGuess ; // a xor b = c means b = a xor c 
                    if (set.contains(otherNumRequired)) {
                        max = greedyGuess;
                        break;
                    }
                }
            }

            return max;
        } 
} 
