```go
func findMaximumXOR(nums []int) int {
    res := 0
    for i := 31; i >= 0; i-- {
        // before res = 00001101
        res <<= 1
        // after  res = 00011010
        pre := make(map[int]bool)
        for _, n := range nums { pre[n >> i] = true }
        // res ^ 1, max res can be 00011011
        // a ^ b = res <=> a ^ res = b
        // try to find if b exist or not.
        for p := range pre { if pre[res ^ 1 ^ p] { res += 1; break } }
    }
    return res
}
