class Trie{
    public :
    int data;
    Trie *pos[2];

    Trie(){
        data = 0;
        pos[0] = pos[1] = NULL;
    }
};

class Solution {
    void add( Trie *root , int data ){
        Trie * temp = root ; // We are assuming 32bits so we are extracting Bits from the MSB even it is 0 in most cases but it will handle larger cases
        for( int i = 31 ; i >= 0 ; i-- ){   
            bool v = ( data >> i ) & 1;  // Extracting Current Bit
            if( temp->pos[ v ] == NULL )  // If Current bit is Not Present then create a Node
                temp->pos[v] = new Trie();
            temp = temp->pos[v];    // traverse the node
        }
        temp->data = data;  // Adding Element to the Leaf Node so that we can do XOR of Elements 
    }  // Above step helps us to remove unnecessary Sum in Finding Max Xor 

    int maxXor( Trie *root , int data ){
        Trie *temp = root;   // Taking temporary Variable to traverse 
        for( int i = 31 ; i >= 0 ; i-- ){
            bool v = ( data >> i ) & 1; // Extracting Current Bit
            if( temp->pos[ 1-v ] != NULL )  // Always got to opposite of current Set Bit if it exist( it will maximize your Value )
                 temp = temp->pos[1-v] ;
            else                     // other wise Go to the same Bit ( No change in value )
                temp = temp->pos[v];
        }
        return temp->data ^ data ; // Directly doing XOR 
    }
public:
    int findMaximumXOR(vector<int>& nums) {
        int ans = 0;
        Trie *root = new Trie();  // Intializng Root of Trie Data Structure
        for( auto &e : nums ){
            add(root , e );  // Adding Nodes for Number
            ans = max( ans , maxXor( root , e ));//  Storing XOR of max value
        }
        return ans;
    }
};

