class Solution {

    private class TrieNode {
        TrieNode left = null; // left points to 0 
        TrieNode right = null; // right points to 1
    }

    public int findMaximumXOR(int[] nums) {
        TrieNode root = new TrieNode(); 

        // Constructing the Trie
        for (int num : nums) {
            TrieNode curr = root;
            for (int i=31; i >= 0; i--) {
                int bit = (num >>> i) & 1;
                if (bit == 0) {
                    if (curr.left == null) {
                        curr.left = new TrieNode();
                    }
                    curr = curr.left;
                } else {
                    if (curr.right == null) {
                        curr.right = new TrieNode();
                    }
                    curr = curr.right;
                }
            }
        }

        int max = Integer.MIN_VALUE;

        // Query on Trie
        for (int num : nums) {
            TrieNode curr = root;
            int currSum = 0;
            for (int i=31; i>=0; i--) {
                int bit = (num >>> i) & 1;
                if (bit == 0) {
                    if (curr.right != null) {
                        currSum += (int)(Math.pow(2, i));
                        // currSum += (1 >> i);
                        curr = curr.right;
                    } else {
                        curr = curr.left;
                    }
                }
                else {
                    if (curr.left != null) {
                        currSum += (int)(Math.pow(2, i));
                        // // currSum += (1 >> i);
                        curr = curr.left;
                    } else {
