class TrieNode {
    var children: [TrieNode?] = Array(repeating: nil, count: 2) // index 0 for 0 bits and 1 for 1 bits
}

class Solution {
    func findMaximumXOR(_ nums: [Int]) -> Int {
        let root = TrieNode()

        // build trie 
        for num in nums {
            var node = root
            for i in stride(from: 30, through: 0, by: -1) {
                let curBit = (num >> i) & 1 // either 0 or 1
                if node.children[curBit] == nil {
                    node.children[curBit] = TrieNode()
                }
                node = node.children[curBit]! // if 0 go children[0], 1 go children[1]
            }
        }

        var globalMax = Int.min
        for num in nums {
            var node = root
            var curMax = 0
            for i in stride(from: 30, through: 0, by: -1) {
                let curBit = (num >> i) & 1
                if let counterPart = node.children[curBit ^ 1] {
                    curMax += 1 << i
                    node = counterPart
                } else {
                    node = node.children[curBit]!
                }
            }
            globalMax = max(globalMax, curMax)
        }

        return globalMax
    }
}
