class node {
public:
    node *next[2];
    node() {
        for(int i = 0; i < 2; i++) next[i] = NULL;
    }
};

class Trie {
public:
    /** Initialize your data structure here. */
    node *root;
    Trie() {
        root = new node();
    }

    /** Inserts a word into the trie. */
    void insert(int num) {
        node *current = root;
        for(int i = 30; i >= 0; i--) {
            bool b = (num >> i) & 1;
            if(current->next[b] == NULL) {
                current->next[b] = new node();
            }
            current = current->next[b];
        }
    }
