        self.children = {}
        self.fin_val = -1


class Trie:
    def __init__(self):
        self.root = TrieNode()


    def insert(self, val):
        ptr = self.root
        for x in range(31, -1, -1):
            digit = 1 if (val & 1 << x) else 0
            if digit not in ptr.children:
                ptr.children[digit] = TrieNode()
            ptr = ptr.children[digit]
        ptr.fin_val = val


