        g_max = 0
        for x in range(len(A)):
            ptr = self.root
            curr = A[x]
            for y in range(31, -1, -1):
                digit = 1 if (curr & 1 << y) else 0
                if digit == 0:
                    if 1 in ptr.children:
                        ptr = ptr.children[1]
                    else:
                        ptr = ptr.children[0]
                else:
                    if 0 in ptr.children:
                        ptr = ptr.children[0]
                    else:
                        ptr = ptr.children[1]
            g_max = max(g_max, ptr.fin_val ^ curr)
