public class Solution
    {
        private class TrieNode
        {
            public readonly TrieNode[] Next = new TrieNode[2];
        }

        private class Trie
        {
            public readonly TrieNode Root = new TrieNode();

            private void AddValue(TrieNode node, int val, int idx)
            {
                if (idx == -1)
                {
                    return;
                }

                int bit = ((val & (1 << idx)) >> idx);

                if (node.Next[bit] == null)
                {
                    node.Next[bit] = new TrieNode();
                }

                AddValue(node.Next[bit], val, idx - 1);
            }

            public void Insert(int val)
            {
                AddValue(Root, val, 31);
            }
        }

        private void Helper(TrieNode node, ref int res, int currentNumber, int bitNumber)
        {
            if (bitNumber < 0)
            {
                return;
            }

            int bitInCurrNumber = ((currentNumber & (1 << bitNumber)) >> bitNumber);
            int opposite = (bitInCurrNumber + 1) % 2;

            if (node.Next[bitInCurrNumber] == null && node.Next[opposite] == null)
            {
                res |= (currentNumber & ((1 << (bitNumber + 1)) - 1));
                return;
            }

            if (node.Next[opposite] != null)
            {
                res |= (1 << bitNumber);
                Helper(node.Next[opposite], ref res, currentNumber, bitNumber - 1);
                return;
            }

            Helper(node.Next[bitInCurrNumber], ref res, currentNumber, bitNumber - 1);
        }

        public int FindMaximumXOR(int[] nums)
        {
            Trie trie = new Trie();

            foreach (var num in nums)
            {
                trie.Insert(num);
            }

            int res = 0;

            foreach (var num in nums)
            {
                int candidate = 0;
                Helper(trie.Root, ref candidate, num, 31);
                res = Math.Max(res, candidate);
            }

            return res;
        }
    }
