class Solution {
    public int findMaximumXOR(int[] nums) {
        int ans = 0, mask = 0;
        for (int x = 31; x >= 0; x--) {
            mask |= (1 << x);
            HashSet<Integer> set = new HashSet<>();
            int temp = ans | (1 << x);
            for (int i = 0; i < nums.length; i++) {
                int num = nums[i] & mask;
                if (set.contains(temp ^ num)) {
                    ans = temp;
                    break;
                }
                set.add(num);
            }
        }     
        return ans;
    }
}
