struct Node {
    Node* child[2];

    bool containsKey(int bit) {
        return (child[bit] != NULL); // checks if this bit is present or not
    }

    void put(int bit, Node* node) {
        child[bit] = node;
    }

    Node* get(int bit) {
        return child[bit];
    }
};

class Trie {
private:
    Node* root;

public:
    Trie() {
        root = new Node();
    }

    void insert(int num) {
        Node* node = root;
        for(int i=31 ; i>=0 ; i--) {
            int bit = (num>>i) & 1;
            if(!node->containsKey(bit)) {
                node->put(bit,new Node());
            }
            node = node->get(bit);
        }
    }

    int getMax(int num) {
        Node* node = root;
        int maxNum = 0;

        for(int i=31 ; i>=0 ; i--) {
            int bit = (num>>i) & 1;
            if(node->containsKey(1-bit)) {
                maxNum = maxNum | (1<<i);
                node = node->get(1-bit);
            }
            else {
                node = node->get(bit);
            }
        }
        return maxNum;
    }
};

class Solution {
public:
    int findMaximumXOR(vector<int>& nums) {
        Trie trie;
        for(auto &ele:nums) {
            trie.insert(ele);
        }

        int maxi = 0;
        for(auto &ele:nums) {
            maxi = max(maxi,trie.getMax(ele));
        }
        return maxi;
    }
};
