class node {
public:
	int data;
	node* left;
	node* right;
	node() {
		left = right = NULL;
	}
};
class Solution {
public:
    node *root =new node();
    //for insertion in trie datastructure
    void insert(int n) {
		node* temp = root;
		for (int i = 31; i >= 0; --i)
		{
			int bit = (n & (1 << i)) ?  1 : 0; 
			if (bit == 1) {
				if (!temp->right) {
					temp->right = new node();
				}
				temp = temp->right;

			}
			else {
				if (!temp->left) {
					temp->left = new node();
				}
				temp = temp->left;
			}
		}
		// after inserting each bit of number in trie then we fill data with that
		temp->data = n;
	}
    	int maxXorPairHelper(int n) {
		node* temp = root;
		for (int i = 31 ; i >= 0 ; i--) {
			int bit = (n & (1 << i)) ? 1 : 0;
			if (bit == 0) {
				if (temp->right) {
					temp = temp->right;
				}
				else {
					temp = temp->left;
				}
			}
			else {
				if (temp->left) {
					temp = temp->left;
				}
				else {
					temp = temp->right;
				}
			}
		}
		return temp->data ^ n;
	}
    int findMaximumXOR(vector<int>& nums) 
    {
       	int ans = INT_MIN;
		for (int i = 0; i < nums.size(); ++i)
		{
			insert(nums[i]);
			int t = maxXorPairHelper(nums[i]);
			ans = max(ans, t);
		}
		return ans;
	} 
