class TrieNode
{
public:
    TrieNode* child[2];
};

class BitWiseTrie
{
public:
    TrieNode* root;

    BitWiseTrie()
    {
        root = new TrieNode();
    }

    // O(L) = O(32) = O(1)
    void insert(int num)
    {
        int currBit = 0;
        TrieNode* currNode = root;       

        for (int i = 31; i >= 0; i--)
        {
            // Right shift i times to move the i-th bit to the leftmost position
            // &1 --> get the bit value
            currBit = (num >> i) & 1;

            if (currNode->child[currBit] == nullptr)
            {
                currNode->child[currBit] = new TrieNode();
            }

            currNode = currNode->child[currBit];
        }
    }

    // O(L) = O(32) = O(1)
    int findMaxXOR(int num)
    {
        int maxXOR = 0;
        int currBit = 0;
        TrieNode* currNode = root;  

        for (int i = 31; i >= 0; i--)
        {
            // Right shift i to move the i-th bit to the leftmost position
            // &1 --> get the bit value
            currBit = (num >> i) & 1;

            // Opposite bit exists --> XOR = 1
            if (currNode->child[!currBit])
            {
                // Move the resulting 1 to the i-th position
                // by left shifting i 
                maxXOR += (1 << i);

                // Move forward to the opposite-bit node
                currNode = currNode->child[!currBit];
            }
            // Opposite bit doesn't exist
            else
            {
                // Move forward to the same-bit node
                currNode = currNode->child[currBit];
            }
        }

        return maxXOR;
    }
};

class Solution 
{
public:
    // T = O(N*L + N*L) = O(N*32 + N*32) = O(N)
    // S = O(2^L) = O(2^32) = O(1)
    int findMaximumXOR(vector<int>& nums) 
    {
        // Insert each number as binary number to the trie
        BitWiseTrie trie;
        for (int i = 0; i < nums.size(); i++)
        {
            trie.insert(nums[i]);
        }

        // For each number, find its max XOR 
        int maxXOR = 0;
        for (int i = 0; i < nums.size(); i++)
        {
            maxXOR = max(maxXOR, trie.findMaxXOR(nums[i]));
        }

        // Return the global max XOR
        return maxXOR;
    }
};
