class TrieNode:
    def __init__(self):
        self.children = {}


class Trie:
    def __init__(self):
        self.root = TrieNode()

    def insert(self, string):
        cur = self.root
        for word in string:
            if word not in cur.children:
                cur.children[word] = TrieNode()
            cur = cur.children[word]

    def search(self, string):
        self.binary = {'1':'0', '0':'1'}
        cur = self.root
        temp = ''
        for word in string:
            if not cur.children:
                break

            if self.binary[word] in cur.children:
                cur = cur.children[self.binary[word]]
                temp += self.binary[word]

            else:
                cur = cur.children[word]
                temp += word


        return int(temp, 2) if temp else int(string, 2)


class Solution:
    def findMaximumXOR(self, num):
        trie = Trie()
        length = len(bin(max(num))[2:])
        ANS = float("-inf")
        for n in num:
            binary = bin(n)[2:]
            binary = (length - len(binary))*'0' + binary
            ANS = max(ANS, n ^ trie.search(binary)) 
            trie.insert(binary)

        return ANS

