/**
 * Definition for singly-linked list.
 * public class ListNode {
 *     int val;
 *     ListNode next;
 *     ListNode() {}
 *     ListNode(int val) { this.val = val; }
 *     ListNode(int val, ListNode next) { this.val = val; this.next = next; }
 * }
 */
class Solution {
    public ListNode swapPairs(ListNode head) {
        if(head == null) return null;

        ListNode dummy = new ListNode(0);
        dummy.next = head;

        ListNode currentPtr = dummy ; // for iteration

        while(currentPtr.next != null && currentPtr.next.next != null ){
            ListNode firstNode = currentPtr.next ; 
            ListNode secondNode  = currentPtr.next.next;
            // swaping nodes by replacing next wrt the currentPtr, which points to dummy atm
            firstNode.next = secondNode.next;
            currentPtr.next = secondNode;
            currentPtr.next.next = firstNode; 

            // now, currentPtr will move to the 2nd node, then currentPtr.next will become first             //   node for the second pair
            currentPtr = currentPtr.next.next;

        }
        // our dummy is empty so after that lies our first node(head of the new list)
        return dummy.next;

    }
}
