    int movesToChessboard(vector<vector<int>>& board) {
        int N = board.size();
        int colMovesNeeded = 0, rowMovesNeeded = 0;
        int onesInFirstCol = 0, onesInFirstRow = 0;
        int zerosInFirstCol = 0, zerosInFirstRow = 0;

        for (int i=1; i<board.size(); i++) {
