        int len = s.length(), sign = 1, res = 0, num = 0;
        for (int i = 0; i < len; i++) {
            char c = s.charAt(i);
            if (c == ' ') continue;
            if (Character.isDigit(c)) {
                num = num * 10 + c - '0';
                if (i == len - 1 || !Character.isDigit(s.charAt(i + 1))) {
                    res += sign * num;
                    num = 0;
