class Solution {
private:
    int calculateUtil(string &s, int &i) {
        int res = 0, curr = 0, prev_oper = 1;
        while(i < s.size()) {

            if(s[i] == '(') 
                curr = calculateUtil(s, ++i);

            else if(s[i] == ')') 
                break;

            else if(s[i] == '+') {
                res += curr*prev_oper;
                prev_oper = 1;
                curr = 0;
            }

            else if(s[i] == '-') {
                res += curr*prev_oper;
                prev_oper = -1;
                curr = 0;
            }

            else if(s[i] != ' ')
                curr = curr*10+(s[i]-'0');

            ++i;
        }

        return res += curr*prev_oper;
    }
public:
    int calculate(string s) {
        int i = 0;
        return calculateUtil(s,i);
    }
};
