class Solution:

    def calculate(self, s: str) -> int:

        queue = deque(s.replace(' ',''))
        return self.helper(queue)

    def helper(self, q):

        res = []       
        num = 0
        sign = '+'

        while q:
            x = q.popleft()
            if x == '(':
                num = self.helper(q) # go to next level
            if x.isdigit():
                num = num * 10 + int(x)
            if not x.isdigit() or not q:
                if sign =='+':
                    res.append(num)
                elif sign =='-':
                    res.append(-1 * num)
                sign = x
                num = 0
            if x == ')':
                break

        return sum(res)
```

