int i = 0;

public int calculate(String s) {
	int num = 0;
	Operation operation = null;
	while (i < s.length()) {
		switch (s.charAt(i)) {
			case ' ':
				i++;
				break;
			case '(':
				i++;
				num = operate(num, operation, calculate(s));
				break;
			case ')':
				i++;
				return num;
			case '+':
				i++;
				operation = Operation.PLUS;
				break;
			case '-':
				i++;
				operation = Operation.MINUS;
				break;
			default:
				int digit = getDigit(s);
				num = operate(num, operation, digit);
				break;
		}
	}
	return num;
}

