private:
    int calculate(string& s, int& i) {
        int res = 0;
        int sign = 1;
        for (; i < s.length(); i++) {
            if (isdigit(s[i])) {
                int start = i;
                while (i + 1 < s.length() && isdigit(s[i + 1])) { i++; }
                res += sign * stoi(s.substr(start, i + 1 - start));
            }
            else if (s[i] == '(') {
                res += sign * calculate(s, ++i);
            }
            else if (s[i] == ')') {
                return res;
            }
            else if (s[i] == '+') {
                sign = 1;
            }
            else if (s[i] == '-') {
                sign = -1;
            }
        }
        return res;
    }
};
