        self.expression = expression
        self.pos = 0
        self.tokens = []

    def lex(self):
        while self.pos < len(self.expression):
            value = self.expression[self.pos]

            if value in ' \r\n\t':
                self.advance()
                continue

            if ttype := KEYWORDS.get(value):
                self.insert(Token(ttype))
            elif value.isdigit():
                self.lex_number()
            else:
                raise ValueError(f'Invalid symbol: {value}')

    def lex_number(self):
        start = self.pos
        self.advance()

        while self.pos < len(self.expression) and self.expression[self.pos].isdigit():
            self.advance()

        end = self.pos
        self.revert()
        self.insert(Token(TokenType.INTEGER, int(self.expression[start:end])))

    def insert(self, token: Token):
        self.tokens.append(token)
        self.advance()

    def advance(self):
        self.pos += 1

    def revert(self):
        self.pos -= 1


class Parser:
    def __init__(self, expression: str):
        lexer = Lexer(expression)
        lexer.lex()
        self.tokens = lexer.tokens
        self.pos = 0

    def parse(self) -> Node:
        return self.parse_plus()

    def parse_plus(self) -> Node:
        result = self.parse_mul()

        while True:
            if self.accept(TokenType.PLUS):
                result = Addition(result, self.parse_mul())
            elif self.accept(TokenType.MINUS):
                result = Subtraction(result, self.parse_mul())
            else:
                break

        return result

    def parse_mul(self) -> Node:
        result = self.parse_unary()

        while True:
            if self.accept(TokenType.MUL):
                result = Multiplication(result, self.parse_unary())
            elif self.accept(TokenType.DIV):
                result = Division(result, self.parse_unary())
            elif self.accept(TokenType.MOD):
                result = Modulus(result, self.parse_unary())
            else:
                break

        return result

    def parse_unary(self) -> Node:
        if self.accept(TokenType.MINUS):
            return UnaryMinus(self.parse_unary())

        return self.parse_primary()

    def parse_primary(self) -> Node:
        if self.accept(TokenType.LPRAREN):
            result = self.parse()
            self.expect(TokenType.RPAREN)
            return result
        elif token := self.accept(TokenType.INTEGER):
            return Literal(token.value)

        raise ValueError(f'Invalid expression')

    def accept(self, ttype: TokenType) -> Optional[Token]:
        current = self.current_token

        if current and current.type == ttype:
            self.advance()
            return current

    def expect(self, ttype: TokenType) -> Token:
        if token := self.accept(ttype):
            return token

        raise ValueError(f'Expected: {ttype}')

    @property
    def current_token(self) -> Optional[Token]:
        if self.pos < len(self.tokens):
            return self.tokens[self.pos]

    def advance(self):
        self.pos += 1


class Visitor(ABC, Generic[T]):
    @abstractmethod
    def visit_addition(self, node: Addition) -> T:
        ...

    @abstractmethod
    def visit_subtraction(self, node: Subtraction) -> T:
        ...

    @abstractmethod
    def visit_multiplication(self, node: Multiplication) -> T:
        ...

    @abstractmethod
    def visit_division(self, node: Division) -> T:
        ...

    @abstractmethod
    def visit_modulus(self, node: Modulus) -> T:
        ...

    @abstractmethod
    def visit_unary_minus(self, node: UnaryMinus) -> T:
        ...

    @abstractmethod
    def visit_literal(self, node: Literal) -> T:
        ...


class Calculator(Visitor[int]):
    def execute(self, node: Node) -> int:
        return node.accept(self)

    def visit_addition(self, node: Addition) -> int:
        return self.execute(node.left) + self.execute(node.right)

    def visit_subtraction(self, node: Subtraction) -> int:
        return self.execute(node.left) - self.execute(node.right)

    def visit_multiplication(self, node: Multiplication) -> int:
        return self.execute(node.left) * self.execute(node.right)

    def visit_division(self, node: Division) -> int:
        return self.execute(node.left) // self.execute(node.right)

    def visit_modulus(self, node: Modulus) -> T:
        return self.execute(node.left) % self.execute(node.right)

    def visit_unary_minus(self, node: UnaryMinus) -> int:
        return -self.execute(node.value)

    def visit_literal(self, node: Literal) -> int:
        return node.value


class Solution:
    def calculate(self, s: str) -> int:
        parser = Parser(s)
        root = parser.parse()

        calculator = Calculator()
        return calculator.execute(root)
