class Solution {
    //Time Complexity O(N)
    //Space Complexity O(N) at most
    public int calculate(String s) {
        if (s == null || s.length() == 0) {
            return 0;
        }

        Pair<Integer, Integer> resultPair = calculateFrom(s, 0);
        return resultPair.getKey();
    }

    public Pair<Integer, Integer> calculateFrom(String s, int fromIdx) {
        if (fromIdx >= s.length()) {
            return new Pair(0, fromIdx + 1);
        }

        int result = 0;
        int sign = 1;
        int i = fromIdx;

        while (i < s.length()) {
            char c = s.charAt(i);

            if (c == '-') {
                sign = -1;
                i++;
                continue;
            }

            if (c == '+' || c == ' ') {
                i++;
                continue;
            }

            if (c == '(') {
                Pair<Integer, Integer> pair = calculateFrom(s, i + 1);
                result += sign * pair.getKey();
                sign = 1;
                i = pair.getValue();
                continue;
            }

            if (c == ')') {
                return new Pair(result, i + 1);
            }

            //else, evaluate next number
            int num = 0;
            while (i < s.length() && Character.isDigit(s.charAt(i))) {
                num = num * 10 + (s.charAt(i) - '0');
                i++;
            }
            result += sign * num;
            sign = 1;
        }        

        return new Pair(result, s.length());
    }
}
