      class Solution {
        public:
            void wiggleSort(vector<int>& nums) {
                int len=nums.size();
                if(len<=1) return;
                sort(nums.begin(), nums.end());
                vector<int> result(nums);
                int j=len-1;
                for(int i=1; i<len; i+=2, j--) result[i]=nums[j];
                for(int i=0; i<len; i+=2, j--) result[i]=nums[j];
                nums=result;
                return;
