public void wiggleSort(int[] nums) {
        if (nums == null || nums.length == 0)
            return;
        Arrays.sort(nums);
        boolean isOdd = (nums.length % 2 == 0)? false: true;

        int mid = 0;
        if (isOdd) {
            mid = nums.length / 2 + 1;
        } else {
            mid = nums.length / 2;
        }

        // Create a Copy
        int[] copy = new int[nums.length];
        for (int i = 0; i < nums.length; i++) {
            copy[i] = nums[i];
        }

        int k = mid - 1;
        int j = nums.length - 1;
        for (int i = 0; i < nums.length; i++) {
            if (i % 2 == 0) {
                nums[i] = copy[k--];
            } else {
                nums[i] = copy[j--];
            }
        }

    }
