class Solution {
    public void wiggleSort(int[] nums) {
        Arrays.sort(nums);
        reverse(nums);
        int[] newnums = new int[nums.length];
        int k = 0;
        int s = 0;
        int e = nums.length  / 2;

        while(k < nums.length){
            if(k % 2 == 0){
                newnums[k++] = nums[e++];
            }else{
                newnums[k++] = nums[s++];
            }
        }
        for(int i = 0; i < nums.length; i++){
            nums[i] = newnums[i];
        }
    }
    private void reverse(int[] nums){
        int s = 0;
        int e = nums.length - 1;
        while(s < e){
            int t = nums[s];
            nums[s] = nums[e];
            nums[e] = t;
            s++;
            e--;
        }
    }
}
