class Solution {
public:
    void wiggleSort(vector<int>& A ) {

        int N = A.size() ;
        sort( A.begin() , A.end() );
        int l = (N-1)/2 ;
        int h = N-1 ; 

        for( int i = 0 ; i < N ; i++ )
        {
            if( !(i&1) ) A[i] = A[i] + (A[l--]%mod)*mod;
            else A[i] = A[i] + (A[h--]%mod)*mod; 
        }
        for( auto &x : A ) x /= mod ; 
    }
};

