class Solution {
public:
    void swap(int &a,int &b){
        int temp=a;
        a=b;
        b=temp;
    }
    int partitionArray(vector<int>& A,int lo,int hi,int pivot){
        int i=lo,j=lo;

        while(i<=hi){
            if(A[i]<=pivot){
                swap(A[i],A[j]);
                i++;
                j++;
            }
            else{
                i++;
            }
        }
        return j-1;
    }
    int quickSelect(vector<int>& A,int lo,int hi,int k){
        int pivot=A[hi];
        int pi=partitionArray(A,lo,hi,pivot);

        if(pi==k){
            return pivot;
        }
        else if(pi>k){
            return quickSelect(A,lo,pi-1,k);
        }
        else{
            return quickSelect(A,pi+1,hi,k);
        }

    }
    void wiggleSort(vector<int>& A) {
        int n=A.size();
        int mid=quickSelect(A,0,n-1,n/2);
         vector<int> aux (A.begin(), A.end());

        for (int i = 0, l = 0, r = n - 1; i < r; ){
            if (aux[i] == mid) i ++;
            else if (aux[i] > mid) swap(aux[i], aux[r --]);
            else swap(aux[l ++], aux[i ++]);
        }

        int m= (n+1)/2;

        for(int i=0,j=m-1;j>=0;i=i+2,j--) A[i]=aux[j];
        for(int i=1,j=n-1;j>=m;i=i+2,j--) A[i]=aux[j];
    }
};
