class Solution {
    //Take the median
    //From the beginning , fill all odd indexes with all values greater than median
    //From the end, fill all even indices with all values SMALLEr than median
    //Fill the rest with median

    public void wiggleSort(int[] nums) {
        int median = findMedian(nums, (nums.length + 1)/2);
        Integer[] temp = new Integer[nums.length];
        int odd = 1;
        int even = nums.length%2 == 0 ? nums.length - 2 : nums.length - 1;
        for(int i = 0; i < nums.length; i++) {
            if(nums[i] > median) {
                temp[odd] = nums[i]; // bigger than median in odd space from beginning
                odd += 2;
            } else if(nums[i] < median) {
                temp[even] = nums[i]; // Smaller than median from end
                even -= 2;
            } 
            //else do nothing
        }

        for(int i = 0; i < temp.length; i++) {
            if(temp[i] == null) temp[i] = median;
            nums[i] = temp[i];
        }
    }

    private int findMedian(int[] nums, int k) {
        PriorityQueue<Integer> heap = new PriorityQueue<>();
        for(int i =0; i < nums.length; i++) {
            if(i < k) {
                heap.offer(nums[i]);
            } else {
                if(nums[i] > heap.peek()) {
                    heap.poll();
                    heap.offer(nums[i]);
                }
            }
        }
        return heap.peek();
    }
}

